package Practice1;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=3758581;
		
		if (num%2==0){
			System.out.println("The given number "+num+" is a even number");
		}
		else {
			System.out.println("The given number "+num+" is a odd number");
		}

	}

}
